import {useState, useEffect } from 'react'
import React from 'react';
import Axios from "axios"
import '../App.css';

function Add() {
  const [listOfCards, setListOfCards] = useState([])
  const [businessName, setCoName] = useState("")
  const [ceoName, setCEOName] = useState("")
  const [numberEmployees, setEmp] = useState("1")
  const [phoneNumber, setNumber] = useState("")
  
  useEffect(() => {
    Axios.get("http://localhost:8000/").then((response) => {
      setListOfCards(response.data);
    });
  }, []);

  const createCard = () => {
    if(document.getElementById("coName").value == "" || document.getElementById("ceoName").value == ""
      || document.getElementById("empCount").value == "" || document.getElementById("phoneNum").value == "") {
      alert("One or more fields is blank. Please fill every field before adding the card")
    }
    else {
      Axios.post("http://localhost:8000/createCards", {
        businessName,
        ceoName,
        numberEmployees,
        phoneNumber,
      }).then((response) => {
        setListOfCards([...listOfCards, {
          businessName,
          ceoName,
          numberEmployees,
          phoneNumber,
        }
        ])
      })
    }
  }

  return (
    <div className="App">
      <h4>Add a Card</h4>
      <div>
        <input type="text" id="coName" placeholder='Company Name' onChange={(event) => {
          setCoName(event.target.value)
        }}/>
        <input type="text" id="ceoName" placeholder='CEO Name' onChange={(event) => {
          setCEOName(event.target.value)
        }}/>
        <input type="text" id="empCount" placeholder='Number of Employees' onChange={(event) => {
          setEmp(event.target.value)
        }}/>
        <input type="text" id="phoneNum" placeholder='Office Phone' onChange={(event) => {
          setNumber(event.target.value)
        }}/>
        <button onClick={createCard}>Create Card</button>
      </div>
    </div>

  );
}

export default Add;
