import {useState, useEffect } from 'react'
import Search from './search';
import React from 'react';
import Axios from "axios"
import '../App.css';
import { response } from 'express';

function Home() {
  const { search } = window.location
  const query = new URLSearchParams(search).get('s')
  const [listOfCards, setListOfCards] = useState([])

  const openPage = v => () => {
    window.open("http://localhost:8000/getCards/" + v)
  }

  useEffect(() => {
    Axios.get("http://localhost:8000/").then((response) => {
      setListOfCards(response.data)
    })
  }, [])

  const deleteCard = v => () => {
    Axios.delete("http://localhost:8000/deleteCard/" + v).then(res => {
      window.location.reload()
    })
  }

  const showCards = () => {
    if (!query){
      Axios.get("http://localhost:8000/").then((res) => {
        setListOfCards(response.data)
      })
      return listOfCards
    }
    const result = listOfCards.filter((card) => 
      card.businessName == query)
    Axios.get("http://localhost:8000/getCards" + result.businessName).then(res => {
      setListOfCards(res.data)
    })
  }

  const SearchBar = () => (
    <form action="/home" method="get">
        <label htmlFor="header-search">
            <span className="visually-hidden">Search business cards</span>
        </label>
        <input
            type="text"
            id="header-search"
            placeholder="Search business cards"
            name="s" 
        />
        <button type="submit" onClick={showCards}>Search</button>
    </form>
);

  return (
    <div className="App">
      <div className='cardsDisplay'>
      <SearchBar/>
        <div className='cardsContainer'>
          {
            listOfCards.map((card) => {
              return (
              <div className='indivCard'>
                <h4 className='title' onClick={openPage(card.businessName)}><b>Company: {card.businessName}</b></h4>
                <h6>CEO Name: {card.ceoName}</h6>
                <h6>Number of Employees: {card.numberEmployees}</h6>
                <h6>Office Phone: {card.phoneNumber}</h6>
                <button className='delete' onClick={deleteCard(card.businessName)}>Delete</button>
              </div>
              )
            })
          }
        </div>
      </div>
    </div>

  );
}

export default Home;
