import {useState, useEffect } from 'react'
import React from 'react';
import Axios from "axios"
import '../App.css';

function Home() {
  const [listOfCards, setListOfCards] = useState([])
  const [businessName, setCoName] = useState("")
  const [ceoName, setCEOName] = useState("")
  const [numberEmployees, setEmp] = useState("")
  const [phoneNumber, setNumber] = useState("")

  useEffect(() => {
    Axios.get("http://localhost:8000/").then((response) => {
      setListOfCards(response.data)
    })
  }, [])

  const updateCard = () => {
    let value = document.getElementById("cards").value
    const URL = "http://localhost:8000/" + value;

    if(document.getElementById("coName").value === "" || document.getElementById("ceoName").value === ""
      || document.getElementById("empCount").value == "" || document.getElementById("phoneNum").value == "") {
      alert("One or more fields is blank. Please fill every field before updating the card")
    }
    else {
      Axios.put(URL, {
        businessName,
        ceoName,
        numberEmployees,
        phoneNumber,
      }).then((response) => {
        setListOfCards([...listOfCards, {
          businessName,
          ceoName,
          numberEmployees,
          phoneNumber,
        }
        ])
      })
      window.location.reload()
    }
  }

  const selectedCard = () => {
    var e = document.getElementById("cards")
    console.log("values: ", e.value)//this.menu.value
    return e.value
  }

  const optionItems = listOfCards.map((card) =>
    <option key={card.businessName}>{card.businessName}</option>
  );

  return (
    <div className="App">
      <div>
        <div className='modPage'>
          <h4>Modify a Card</h4>
          <select name="cards" id="cards" onChange={selectedCard} refs="selectedItem">
            {optionItems}
          </select>
            <div className='updateCard'>
              <input type="text" id="coName" placeholder='Company Name' onChange={(event) => {
                setCoName(event.target.value)
              }}/>
              <input type="text" id="ceoName" placeholder='CEO Name' onChange={(event) => {
                setCEOName(event.target.value)
              }}/>
              <input type="text" id="empCount" placeholder='Number of Employees' onChange={(event) => {
                setEmp(event.target.value)
              }}/>
              <input type="text" id="phoneNum" placeholder='Office Phone' onChange={(event) => {
                setNumber(event.target.value)
              }}/>
            </div>
        </div>
        <button onClick={updateCard}>Update Card</button>
      </div>
    </div>

  );
}

export default Home;
