import {useState, useEffect } from 'react'
import Search from './search';
import React from 'react';
import Axios from "axios"
import '../App.css';
import { response } from 'express';

function Home() {
  // const { search } = window.location
  // const query = new URLSearchParams(search).get('s')
  const [listOfCards, setListOfCards] = useState([])

  const openPage = v => () => {
    window.open("http://localhost:8000/getCards/" + v)
  }

  useEffect(() => {
    Axios.get("http://localhost:8000/").then((response) => {
      setListOfCards(response.data)
    })
  }, [])

  const deleteCard = v => () => {
    Axios.delete("http://localhost:8000/deleteCard/" + v).then(res => {
      window.location.reload()
    })
  }

  return (
    <div className="App">
      <div className='cardsDisplay'>
        <div className='cardsContainer'>
          {
            listOfCards.map((card) => {
              return (
              <div className='indivCard'>
                <h4 className='title' onClick={openPage(card.businessName)}><b>Company: {card.businessName}</b></h4>
                <h6>CEO Name: {card.ceoName}</h6>
                <h6>Number of Employees: {card.numberEmployees}</h6>
                <h6>Office Phone: {card.phoneNumber}</h6>
                <button className='delete' onClick={deleteCard(card.businessName)}>Delete</button>
              </div>
              )
            })
          }
        </div>
      </div>
    </div>

  );
}

export default Home;
